mapper.select:
select 

from activity-comment-comment
where a_c_c_id = #{aCCId,jdbcType=VARCHAR}
  and act_com_id = #{actComId,jdbcType=VARCHAR}
  and username = #{username,jdbcType=VARCHAR}

mapper.delete:
delete from activity-comment-comment
where a_c_c_id = #{aCCId,jdbcType=VARCHAR}
  and act_com_id = #{actComId,jdbcType=VARCHAR}
  and username = #{username,jdbcType=VARCHAR}

mapper.update:
update activity-comment-comment


    a_c_c_content = #{aCCContent,jdbcType=VARCHAR},


    a_c_c_time = #{aCCTime,jdbcType=TIMESTAMP},


where a_c_c_id = #{aCCId,jdbcType=VARCHAR}
  and act_com_id = #{actComId,jdbcType=VARCHAR}
  and username = #{username,jdbcType=VARCHAR}

update activity-comment-comment
set a_c_c_content = #{aCCContent,jdbcType=VARCHAR},
  a_c_c_time = #{aCCTime,jdbcType=TIMESTAMP}
where a_c_c_id = #{aCCId,jdbcType=VARCHAR}
  and act_com_id = #{actComId,jdbcType=VARCHAR}
  and username = #{username,jdbcType=VARCHAR}
