mapper.select:
select 

from activity-comment
where act_com_id = #{actComId,jdbcType=VARCHAR}
  and username = #{username,jdbcType=VARCHAR}

mapper.delete:
delete from activity-comment
where act_com_id = #{actComId,jdbcType=VARCHAR}
  and username = #{username,jdbcType=VARCHAR}

mapper.update:
update activity-comment


    act_com_content = #{actComContent,jdbcType=VARCHAR},


    act-com_time = #{actComTime,jdbcType=TIMESTAMP},


    act_com_like = #{actComLike,jdbcType=INTEGER},


where act_com_id = #{actComId,jdbcType=VARCHAR}
  and username = #{username,jdbcType=VARCHAR}

update activity-comment
set act_com_content = #{actComContent,jdbcType=VARCHAR},
  act-com_time = #{actComTime,jdbcType=TIMESTAMP},
  act_com_like = #{actComLike,jdbcType=INTEGER}
where act_com_id = #{actComId,jdbcType=VARCHAR}
  and username = #{username,jdbcType=VARCHAR}
