mapper.select:
select 

from study-reply
where std_id = #{stdId,jdbcType=VARCHAR}
  and username = #{username,jdbcType=VARCHAR}

mapper.delete:
delete from study-reply
where std_id = #{stdId,jdbcType=VARCHAR}
  and username = #{username,jdbcType=VARCHAR}

mapper.update:
update study-reply


    std_re_content = #{stdReContent,jdbcType=VARCHAR},


    std_re_time = #{stdReTime,jdbcType=TIMESTAMP},


    std_re_like = #{stdReLike,jdbcType=INTEGER},


where std_id = #{stdId,jdbcType=VARCHAR}
  and username = #{username,jdbcType=VARCHAR}

update study-reply
set std_re_content = #{stdReContent,jdbcType=VARCHAR},
  std_re_time = #{stdReTime,jdbcType=TIMESTAMP},
  std_re_like = #{stdReLike,jdbcType=INTEGER}
where std_id = #{stdId,jdbcType=VARCHAR}
  and username = #{username,jdbcType=VARCHAR}

