mapper.select:
select 

from user_activity
where act_id = #{actId,jdbcType=INTEGER}
  and username = #{username,jdbcType=VARCHAR}

mapper.delete:
delete from user_activity
where act_id = #{actId,jdbcType=INTEGER}
  and username = #{username,jdbcType=VARCHAR}

mapper.insert:
insert into user_activity (act_id, username, flag
  )
values (#{actId,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{flag,jdbcType=INTEGER}
  )

mapper.update:
update user_activity


    flag = #{flag,jdbcType=INTEGER},


where act_id = #{actId,jdbcType=INTEGER}
  and username = #{username,jdbcType=VARCHAR}

update user_activity
set flag = #{flag,jdbcType=INTEGER}
where act_id = #{actId,jdbcType=INTEGER}
  and username = #{username,jdbcType=VARCHAR}
